console.log(`DEV_MODE: ${devMode || 'NOT SET'}`);
if (devMode === 'true' && nodeEnv === 'production') {
  errors.push('CRITICAL: DEV_MODE must not be "true" in production (security risk)');
}

console.log('\nüìã VALIDATION RESULTS');
