import { useState, useEffect, useRef } from 'react';
import { Send, ArrowLeft } from 'lucide-react';
import mainGroupInternal from '@/assets/main_group_internal.jpg';

interface User {
  id: number;
  anonName: string;
}

interface Message {
  id: number;
  content: string;
  createdAt: string;
  user: User | null;
}

interface ChatInterfaceProps {
  user: User;
  messages: Message[];
  onSendMessage: (content: string) => void;
  isConnected: boolean;
  onlineCount?: number;
}

export default function ChatInterface({ 
  user, 
  messages, 
  onSendMessage, 
  isConnected,
  onlineCount = 0
}: ChatInterfaceProps) {
  const [messageText, setMessageText] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = () => {
    const content = messageText.trim();
    if (!content || !isConnected) return;

    onSendMessage(content);
    setMessageText('');

    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessageText(e.target.value);

    // Auto-resize textarea
    const textarea = e.target;
    textarea.style.height = 'auto';
    textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
  };

  const getAvatarColor = (userId: number) => {
    const colors = [
      'bg-cyan-600',
      'bg-purple-600',
      'bg-emerald-600',
      'bg-rose-600',
      'bg-indigo-600',
      'bg-teal-600',
    ];
    return colors[userId % colors.length];
  };

  const getInitials = (name: string) => {
    return name.slice(0, 2).toUpperCase();
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('ru-RU', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="h-full flex flex-col bg-black">

      {/* Chat Header */}
      <header className="bg-black border-b border-zinc-800 px-4 py-3 flex items-center justify-between">
        <div className="flex items-center">
          <button 
            onClick={() => window.history.back()}
            className="p-2 mr-2 hover:bg-zinc-800 rounded-full transition-colors"
            data-testid="button-back"
          >
            <ArrowLeft className="w-5 h-5 text-white" />
          </button>
          <div className="w-8 h-8 rounded-full overflow-hidden mr-3">
            <img 
              src={mainGroupInternal} 
              alt="Group Internal" 
              className="w-full h-full object-cover"
            />
          </div>
          <div>
            <h1 className="font-semibold text-white">–ê–Ω–æ–Ω–∏–º–Ω—ã–π —á–∞—Ç</h1>
            <p className="text-xs text-zinc-400" data-testid="text-online-count">
              {onlineCount} —É—á–∞—Å—Ç–Ω–∏–∫–æ–≤ –æ–Ω–ª–∞–π–Ω
            </p>
          </div>
        </div>
        <button className="p-2 hover:bg-zinc-800 rounded-full transition-colors">
          <svg className="w-5 h-5 text-zinc-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z"></path>
          </svg>
        </button>
      </header>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto px-4 py-4 space-y-4 bg-black">
        {messages.length === 0 ? (
          <div className="text-center py-8">
            <div className="w-16 h-16 bg-zinc-800 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-zinc-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"></path>
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-white mb-2">
              –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ –∞–Ω–æ–Ω–∏–º–Ω—ã–π —á–∞—Ç!
            </h3>
            <p className="text-zinc-400 text-sm max-w-xs mx-auto">
              –û–±—â–∞–π—Ç–µ—Å—å –∞–Ω–æ–Ω–∏–º–Ω–æ —Å –¥—Ä—É–≥–∏–º–∏ —É—á–∞—Å—Ç–Ω–∏–∫–∞–º–∏. –ë—É–¥—å—Ç–µ –≤–∑–∞–∏–º–Ω–æ –≤–µ–∂–ª–∏–≤—ã.
            </p>
          </div>
        ) : (
          messages.map((message) => {
            const isCurrentUser = message.user?.id === user.id;

            return (
              <div 
                key={`${message.id}-${message.createdAt}`}
                className={`flex items-start space-x-3 ${isCurrentUser ? 'flex-row-reverse' : ''}`}
                data-testid={`message-${message.id}`}
              >
                <div className={`w-8 h-8 ${
                  isCurrentUser 
                    ? 'bg-blue-600' 
                    : getAvatarColor(message.user?.id || 0)
                } rounded-full flex items-center justify-center flex-shrink-0`}>
                  <span className="text-white text-xs font-semibold">
                    {message.user ? getInitials(message.user.anonName) : '?'}
                  </span>
                </div>

                <div className={`flex-1 min-w-0 ${isCurrentUser ? 'text-right' : ''}`}>
                  <div className={`flex items-baseline space-x-2 mb-1 ${isCurrentUser ? 'justify-end' : ''}`}>
                    {isCurrentUser ? (
                      <>
                        <span className="text-xs text-zinc-500">
                          {formatTime(message.createdAt)}
                        </span>
                        <span className="text-sm font-medium text-white">–í—ã</span>
                      </>
                    ) : (
                      <>
                        <span className="text-sm font-medium text-white">
                          {message.user?.anonName || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π'}
                        </span>
                        <span className="text-xs text-zinc-500">
                          {formatTime(message.createdAt)}
                        </span>
                      </>
                    )}
                  </div>

                  <div className={`rounded-lg px-3 py-2 ${
                    isCurrentUser 
                      ? 'bg-blue-600 text-white rounded-tr-sm' 
                      : 'bg-zinc-800 border border-zinc-700 rounded-tl-sm text-white'
                  }`}>
                    <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                  </div>
                </div>
              </div>
            );
          })
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <div className="bg-black border-t border-zinc-800 px-4 py-3">
        <div className="flex items-end space-x-2">
          <div className="flex-1 relative">
            <textarea
              ref={textareaRef}
              value={messageText}
              onChange={handleInputChange}
              onKeyDown={handleKeyDown}
              placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
              className="w-full resize-none bg-black border border-zinc-700 rounded-2xl px-4 py-3 pr-4 text-sm text-white placeholder-zinc-500 focus:outline-none focus:border-zinc-600 transition-colors"
              rows={1}
              maxLength={1000}
              disabled={!isConnected}
              data-testid="input-message"
            />
          </div>

          <button 
            onClick={handleSend}
            disabled={!messageText.trim() || !isConnected}
            className="bg-purple-600 text-white rounded-full w-12 h-12 flex items-center justify-center hover:bg-purple-700 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed disabled:bg-zinc-700 shadow-lg shadow-purple-500/50 hover:shadow-xl hover:shadow-purple-400/60 ring-2 ring-purple-500/30 hover:ring-purple-400/50"
            data-testid="button-send"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        <div className="mt-2 text-right">
          <span className="text-xs text-zinc-500" data-testid="text-char-counter">
            {messageText.length}/1000
          </span>
        </div>
      </div>
    </div>
  );
}

