const AUTH_STATE_KEY = 'chat_auth_state';
const TOKEN_REFRESH_BUFFER = 2 * 60 * 1000; // 2 minutes before expiry
const TOKEN_LIFETIME = 15 * 60 * 1000; // 15 minutes in milliseconds

class AuthManager {
  private authState: AuthState = {
    user: null,
    token: null,
    refreshToken: null,
    status: 'loading'
  };

